import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  Button,
  Box,
  Chip,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  TextField,
  DialogActions,
  Alert,
  Fab
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  TrendingUp,
  TrendingDown
} from '@mui/icons-material';
import { WatchedStock, WatchedStockRequest } from '../types';
import { watchlistService } from '../services/api';

const Dashboard: React.FC = () => {
  const [watchedStocks, setWatchedStocks] = useState<WatchedStock[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [dialogOpen, setDialogOpen] = useState(false);
  const [editingStock, setEditingStock] = useState<WatchedStock | null>(null);
  const [stockForm, setStockForm] = useState<WatchedStockRequest>({
    symbol: '',
    minPrice: undefined,
    maxPrice: undefined
  });

  useEffect(() => {
    loadWatchlist();
  }, []);

  const loadWatchlist = async () => {
    try {
      const stocks = await watchlistService.getWatchlist();
      setWatchedStocks(stocks);
    } catch (err) {
      setError('Błąd podczas ładowania listy obserwowanych akcji');
    } finally {
      setLoading(false);
    }
  };

  const handleAddStock = () => {
    setEditingStock(null);
    setStockForm({ symbol: '', minPrice: undefined, maxPrice: undefined });
    setDialogOpen(true);
  };

  const handleEditStock = (stock: WatchedStock) => {
    setEditingStock(stock);
    setStockForm({
      symbol: stock.symbol,
      minPrice: stock.minPrice,
      maxPrice: stock.maxPrice
    });
    setDialogOpen(true);
  };

  const handleSaveStock = async () => {
    try {
      if (editingStock) {
        await watchlistService.updateStock(editingStock.id, stockForm);
      } else {
        await watchlistService.addStock(stockForm);
      }
      setDialogOpen(false);
      loadWatchlist();
    } catch (err) {
      setError('Błąd podczas zapisywania akcji');
    }
  };

  const handleDeleteStock = async (id: number) => {
    if (window.confirm('Czy na pewno chcesz usunąć tę akcję z obserwowanych?')) {
      try {
        await watchlistService.removeStock(id);
        loadWatchlist();
      } catch (err) {
        setError('Błąd podczas usuwania akcji');
      }
    }
  };

  if (loading) {
    return (
      <Container>
        <Typography variant="h4" align="center" sx={{ mt: 4 }}>
          Ładowanie...
        </Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4" component="h1">
          📊 Moje Akcje
        </Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={handleAddStock}
          size="large"
        >
          Dodaj Akcję
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {watchedStocks.length === 0 ? (
        <Card sx={{ textAlign: 'center', py: 8 }}>
          <CardContent>
            <Typography variant="h6" color="textSecondary">
              Nie obserwujesz jeszcze żadnych akcji
            </Typography>
            <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
              Kliknij "Dodaj Akcję" aby zacząć obserwować kursy akcji
            </Typography>
          </CardContent>
        </Card>
      ) : (
        <Grid container spacing={3}>
          {watchedStocks.map((stock) => (
            <Grid item xs={12} sm={6} md={4} key={stock.id}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <CardContent sx={{ flexGrow: 1 }}>
                  <Box display="flex" justifyContent="space-between" alignItems="flex-start" mb={2}>
                    <Typography variant="h5" component="h2" fontWeight="bold">
                      {stock.symbol}
                    </Typography>
                    <Chip
                      label={stock.active ? 'Aktywna' : 'Nieaktywna'}
                      color={stock.active ? 'success' : 'default'}
                      size="small"
                    />
                  </Box>

                  <Box mb={2}>
                    {stock.minPrice && (
                      <Box display="flex" alignItems="center" mb={1}>
                        <TrendingDown color="error" sx={{ mr: 1 }} />
                        <Typography variant="body2">
                          Min. cena: <strong>${stock.minPrice}</strong>
                        </Typography>
                      </Box>
                    )}
                    {stock.maxPrice && (
                      <Box display="flex" alignItems="center">
                        <TrendingUp color="success" sx={{ mr: 1 }} />
                        <Typography variant="body2">
                          Max. cena: <strong>${stock.maxPrice}</strong>
                        </Typography>
                      </Box>
                    )}
                  </Box>

                  <Typography variant="caption" color="textSecondary">
                    Dodano: {new Date(stock.createdAt).toLocaleDateString('pl-PL')}
                  </Typography>
                </CardContent>

                <Box sx={{ p: 2, pt: 0 }}>
                  <Button
                    size="small"
                    startIcon={<EditIcon />}
                    onClick={() => handleEditStock(stock)}
                    sx={{ mr: 1 }}
                  >
                    Edytuj
                  </Button>
                  <Button
                    size="small"
                    color="error"
                    startIcon={<DeleteIcon />}
                    onClick={() => handleDeleteStock(stock.id)}
                  >
                    Usuń
                  </Button>
                </Box>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}

      {/* Dialog dodawania/edycji akcji */}
      <Dialog open={dialogOpen} onClose={() => setDialogOpen(false)} maxWidth="sm" fullWidth>
        <DialogTitle>
          {editingStock ? 'Edytuj Akcję' : 'Dodaj Nową Akcję'}
        </DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Symbol akcji (np. AAPL)"
            fullWidth
            variant="outlined"
            value={stockForm.symbol}
            onChange={(e) => setStockForm({ ...stockForm, symbol: e.target.value.toUpperCase() })}
            disabled={!!editingStock}
            sx={{ mb: 2 }}
          />
          <TextField
            margin="dense"
            label="Minimalna cena (alert gdy spadnie poniżej)"
            fullWidth
            variant="outlined"
            type="number"
            value={stockForm.minPrice || ''}
            onChange={(e) => setStockForm({
              ...stockForm,
              minPrice: e.target.value ? parseFloat(e.target.value) : undefined
            })}
            sx={{ mb: 2 }}
          />
          <TextField
            margin="dense"
            label="Maksymalna cena (alert gdy wzrośnie powyżej)"
            fullWidth
            variant="outlined"
            type="number"
            value={stockForm.maxPrice || ''}
            onChange={(e) => setStockForm({
              ...stockForm,
              maxPrice: e.target.value ? parseFloat(e.target.value) : undefined
            })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDialogOpen(false)}>Anuluj</Button>
          <Button
            onClick={handleSaveStock}
            variant="contained"
            disabled={!stockForm.symbol}
          >
            {editingStock ? 'Zapisz' : 'Dodaj'}
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Dashboard;
